import React from "react";
import "./styles.css";
import faqList from "../../data/faqList";

const classes = "flex justify-content-between align-items-center";

function Faqs() {
  // const deleteHandler = (e) => {};
  const collapseHandler = () => {};

  return (
    <div className="flex justify-content-center mt-75 w-100 h-50">
      <div className="w-50" data-testid="faq-list">
        <FaqTemplate
          question={faqList[0].question}
          answer={faqList[0].answer}
          // deleteHandler={deleteHandler}
        />
        <FaqTemplate
          question={faqList[1].question}
          answer={faqList[1].answer}
          // deleteHandler={deleteHandler}
        />
        <FaqTemplate
          question={faqList[2].question}
          answer={faqList[2].answer}
          // deleteHandler={deleteHandler}
        />
      </div>
      <div className={classes}>
        <div>
          <textarea
            className="ma-8 w-100"
            placeholder="Enter the question"
            data-testid="add-question"
            // value=""
            defaultValue={""}
          />
          <textarea
            className="ma-8 w-100"
            placeholder="Enter the answer"
            data-testid="add-answer"
            // value=""
            defaultValue={""}
          />
        </div>
        <button data-testid="add-faq-button">Add</button>
      </div>
    </div>
  );
}

const FaqTemplate = (props) => {
  return (
    <>
      <div className="flex justify-content-center mt-75 w-100 h-50">
        <div className="w-50" data-testid="faq-list">
          <div className="item my-10">
            <div className={`ques ${classes} pa-15 mb-3`}>
              <h4 className="flex align-items-center my-2">{props.question}</h4>
              <span>{"+"}</span>
            </div>
            <div className={`closed open ${classes}`}>
              <p className="my-0">{props.answer}</p>
              <div className={classes}>
                <button className="danger small" onClick={props.deleteHandler}>
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Faqs;
